mapper.insert:
  	INSERT INTO MEMBER(
	    ID
	    , PW
	    , NICK
	)VALUES(
	    #{id}
	    , #{pw}
	    , #{nick}
	)

mapper.select:
SELECT *
FROM MEMBER
WHERE ID = #{id}
AND PW = #{pw}
AND QUIT_YN = 'N'

mapper.update:
UPDATE MEMBER
    SET PW = #{pw}
    , UPDATED_AT = SYSDATE
WHERE NO = #{no}
AND QUIT_YN = 'N'

UPDATE MEMBER
    SET NICK = #{nick}
    , UPDATED_AT = SYSDATE
WHERE NO = #{no}
AND QUIT_YN = 'N'

UPDATE MEMBER
    SET QUIT_YN = 'Y'
WHERE NO = #{no}
